binding.script:

var hdg_prop = "/aircraft/afcs/crs2";
var nav_prop = "/instrumentation/nav[1]/radials/selected-deg";

if (359 > getprop(hdg_prop))
	setprop(hdg_prop, getprop(hdg_prop) + 1);
else
	setprop(hdg_prop, 0);

setprop(nav_prop, getprop(hdg_prop));


var hdg_prop = "/aircraft/afcs/crs2";
var nav_prop = "/instrumentation/nav[1]/radials/selected-deg";

