if (359 > getprop(hdg_prop))
	setprop(hdg_prop, getprop(hdg_prop) + 1);
else
	setprop(hdg_prop, 0);


var hdg_prop = "/aircraft/afcs/hdg-setting";

if (getprop(hdg_prop) > 0)
	setprop(hdg_prop, getprop(hdg_prop) - 1);
else
	setprop(hdg_prop, 359);


if (getprop("/aircraft/afcs/ver-mode") == "vs") {

	setprop("/aircraft/afcs/vs-setting", getprop("/aircraft/afcs/vs-setting") - 100);

} else {

	setprop("/aircraft/afcs/ias-setting", getprop("/aircraft/afcs/ias-setting") + 1);

}


